package com.adventofcode.year2016.day2;

import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.assertEquals;

public class TwentySixteenDay2Test {

    @Test
    void testDetermineBathroomCodeOnNumericKeypadSample() {
        String input = """
                ULL
                RRDDD
                LURDL
                UUUUD""";

        assertEquals("1985", TwentySixteenDay2.determineBathroomCodeOnNumericKeypad(input), "Bathroom code does not match expected value");
    }

    @Test
    void testDetermineBathroomCodePuzzleOnNumericKeypadInput() {
        String input = """
                
                
                
                
                """;

        assertEquals("78293", TwentySixteenDay2.determineBathroomCodeOnNumericKeypad(input), "Bathroom code does not match expected value");
    }

    @Test
    void testDetermineBathroomCodeOnDiamondKeypadSample() {
        String input = """
                ULL
                RRDDD
                LURDL
                UUUUD""";

        assertEquals("5DB3", TwentySixteenDay2.determineBathroomCodeOnDiamondKeypad(input), "Bathroom code does not match expected value");
    }

    @Test
    void testDetermineBathroomCodePuzzleOnDiamondKeypadInput() {
        String input = """
                
                
                
                
                """;

        assertEquals("AC8C8", TwentySixteenDay2.determineBathroomCodeOnDiamondKeypad(input), "Bathroom code does not match expected value");
    }
}
